import React, { useEffect, useState } from "react";
import { RouteComponentProps } from "react-router-dom";
import axios from "axios";

interface JobDetailsProps extends RouteComponentProps<{ id: string }> {}

const JobDetails: React.FC<JobDetailsProps> = ({ match }) => {
  const [job, setJob] = useState(null);

  useEffect(() => {
    axios
      .get(`/job/${match.params.id}`)
      .then((response) => setJob(response.data))
      .catch((error) => console.error(error));
  }, [match.params.id]);

  if (!job) return <div>Loading...</div>;

  return (
    <div>
      <h1>Job Details</h1>
      <p>Customer: {job.customerName}</p>
      <p>Job type: {job.jobType}</p>
      <p>Status: {job.status}</p>
      <p>Appointment Date: {new Date(job.appointmentDate).toLocaleString()}</p>
      <p>Technician: {job.technician}</p>
    </div>
  );
};

export default JobDetails;
